DELIMITER $$
CREATE TRIGGER before_insert_brands
    BEFORE INSERT ON brands
    FOR EACH ROW
BEGIN
    IF NEW.id IS NULL THEN
        SET NEW.id = UUID_TO_BIN(UUID());
    END IF;
END$$
DELIMITER ;


